import React, {Component} from 'react';
import './CreateNewSongForm.css'


class CreateNewSongForm extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            title: '',
            artist: '',
            album: '',
            release_date: '',
            genre: ''
         }
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.props.createNewSong(this.state)
    }

    render() { 
        return ( 
            <div className='container'>
                <div className='row'>
                        <div className='col-sm-12 col-md-12 col-xl-12'>
                            <form onSubmit={this.handleSubmit}>
                                <div className='form-row'>
                                    <input name="title" onChange={this.handleChange} value={this.state.title} placeholder='Title'/>

                                    <input name="artist" onChange={this.handleChange} value={this.state.artist} placeholder='Artist'/>

                                    <input name="album" onChange={this.handleChange} value={this.state.album} placeholder='Album'/>

                                    <input name="release_date" onChange={this.handleChange} type='date' value={this.state.release_date} placeholder='Release Date'/>

                                    <input name="genre" onChange={this.handleChange} value={this.state.genre} placeholder='Genre'/>
                                    <button type="submit">Add Song</button>
                                </div>
                            </form> 
                        </div>
                </div>
            </div>
        );
    }
}

export default CreateNewSongForm;
